mapper.select:
select 
    from GAME_AWARD 
    where 
    LOTTERYID = #{lotteryId}
    and AWARD_GROUP_ID = #{awardGroupId}

    	and STATUS = #{status}

    order by game_group_code, game_set_code,bet_method_code

select 
    from GAME_AWARD 
    where 
    LOTTERYID = #{lotteryId}
    and AWARD_GROUP_ID = #{awardGroupId}
    and GAME_GROUP_CODE = #{gameGroupCode}
    and GAME_SET_CODE = #{gameSetCode}
    and BET_METHOD_CODE = #{betMethodCode}
